package basic;
import java.util.*;
public class Sum_Of_Two_Number {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc=new Scanner(System.in);
		int a,b,sum;
		System.out.print("Enter your first number\n");
		a=sc.nextInt();
		System.out.print("Enter your 2nd number\n");
		b=sc.nextInt();
		sum=a+b;
		System.out.print("Sum of Two Number is= "+sum);
		
	}

}
